
var sctollTop;
$(window).resize(function(){
	sctollTop = $('.header-top-nav').outerHeight();

	if ( $(window).width() < 992) {
		$('.nav-list').appendTo('#menu-mobile');
	} else {
		$('#nav-list').insertBefore('.search-wrap ');
	}
	$('.wrapper').each(function(){
		var highestBox = 0;
		$('.blok-news ', this).each(function(){
			if($(this).height() > highestBox) {
				highestBox = $(this).height();
			}
		});
		$('.blok-news ',this).height(highestBox);
	});
});$(window).resize();

$(document).ready(function() {
	$('.button-search').click(function() {
		$('.search-wrap').toggleClass('search-active');
	});

	$('.top-slider').slick({
		dots: true,
		infinite: true,
		arrows: false,
		responsive: [
		{
			breakpoint: 1368,
			settings: {
				slidesToShow: 1,
				slidesToScroll: 1,
			}
		}
		]
	});


	// var sctollTop = 50;
	// var sctollTop = $('.header-top-nav').outerHeight();
	$(window).scroll(function(event) {
		if ( sctollTop < $(window).scrollTop()) {
			$('#navigation').addClass('fixed');
			$('.top-slider').addClass('fixed');
		} else {
			$('#navigation').removeClass('fixed');
			$('.top-slider').removeClass('fixed');
		}
	});

	$(" #nav-toggle").click(function(){
		if ($(window).width() <= 992 ) {
			menuToggle();
		} 
	});

	$(".drop-menu").click(function(event){
		
		if ($(window).width() <= 992 ) {
			event.preventDefault();
			$('.sub-menu').slideToggle();
		} 
	});
	$(".oll-servs").click(function(event){
		$('.servs-list').slideToggle();
	});


	function menuToggle() {
		// var menuHeight = $('.nav-wrap').height();
		// $('#navigation .contact-nav-wrap').css({top: menuHeight});
		$('#nav-toggle').toggleClass("active");
		// $("#menu-mobile").toggleClass("active");
		$("#menu-mobile").slideToggle(400);
	};
});